        "    'Tier 1',\n",
        "    18.3,\n",
        "    8.5,\n",
        "    10.0,\n",
        "    12.0\n",
        "\n",
        "UNION ALL\n",
        "\n",
        "SELECT\n",
        "    'Total Capital',\n",
        "    21.7,\n",
        "    10.5,\n",
        "    13.0,\n",
        "    16.0"
      ]
    }
